import React, { useState, useEffect } from 'react';
import Item from './Item';
import Button from './Button';

// Parte pensante -> Use: https://pokeapi.co

const Table = () => {

  const [list, setList] = useState([]); 
  const [buttons, setButtons] = useState({previous: null, next: null});

  // Estado inicial 
  useEffect(() => {
    setList(request('https://pokeapi.co/api/v2/pokemon/'));
  }, []);

  // Requisição à API
  function request (url) {
    let xhr = new XMLHttpRequest();
    xhr.open("GET", url);

    xhr.onload = function (e) {
      if (xhr.readyState === 4) {
        if (xhr.status === 200) {
          const json = JSON.parse(xhr.responseText);

          console.log(json);

          setButtons({ ...buttons, previous: json.previous });
          setButtons({ ...buttons, next: json.next});

          return json.results;
        } else {
          console.error(xhr.statusText);
        }
      }
    };
    xhr.onerror = function (e) {
      console.error(xhr.statusText);
    };

    xhr.send(null);

  }

  function getPrevious(url) {
    setList(request(url));
  }

  function getNext(url) {
    setList(request(url));
  }

  return (
    <>
      <div>

        {
            (list) ? <div>Carregando</div> : ""

        }

      </div>

      <div className="flex justify-center mt-10">
        {!!buttons.previous && <Button func={() => getPrevious(buttons.previous)} content="Previous" />}
        {!!buttons.next && <Button func={() => getNext(buttons.next)} content="Next" /> }
      </div>  
    </>
  );
}

export default Table;
