import React, { useState, useEffect } from 'react';
import Item from './Item';
import Button from './Button';

// Parte pensante -> Use: https://pokeapi.co

const Table = () => {

  const [list, setList] = useState([ { name: "Charmander" }, { name: "Bubassalro"} ]); 
  const [buttons, setButtons] = useState({previous: null, next: null});

  // Estado inicial 
  useEffect(() => {
    request('https://pokeapi.co/api/v2/pokemon/');
  }, []);

  // Requisição à API
  function request (url) {
    let xhr = new XMLHttpRequest();
    xhr.open("GET", url);

    xhr.onload = function (e) {
      if (xhr.readyState === 4) {
        if (xhr.status === 200) {
          const json = JSON.parse(xhr.responseText);

          if(json.responseText.previous != null) {
            setButtons({ ...buttons, previous: json.previous })
          }

          if(json.responseText.next != null) {
            setButtons({ ...buttons, next: json.responseText.next})
          }

          console.log(buttons)
        } else {
          console.error(xhr.statusText);
        }
      }
    };
    xhr.onerror = function (e) {
      console.error(xhr.statusText);
    };

    xhr.send(null);

  }

  function getPrevious() {
    console.log('Previous')
  }

  function getNext() {
    console.log('next')
  }

  return (
    <>
      <div>
        {
          list.map((item) => {
            return (
              <Item key={item.name} name={item.name} ></Item>
            );
          })
        }

      </div>

      <div className="flex justify-center mt-10">
        {!!buttons.previous && <Button func={() => getPrevious()} content="Previous" />}
        {!!buttons.next && <Button func={() => getNext()} content="Next" /> }
      </div>  
    </>
  );
}

export default Table;
