import React, { useState } from 'react';
import Item from './Item';
import Button from './Button';

// Parte pensante -> Use: https://pokeapi.co

const Table = () => {

  const [list, setList] = useState([ { name: "Charmander" }, { name: "Bubassalro"} ]); 

  function request () {
    let xhr = new XMLHttpRequest();
    xhr.open("GET", "/bar/foo.txt", true);
    xhr.onload = function (e) {
      if (xhr.readyState === 4) {
        if (xhr.status === 200) {
          console.log(xhr.responseText);
        } else {
          console.error(xhr.statusText);
        }
      }
    };
    xhr.onerror = function (e) {
      console.error(xhr.statusText);
    };
    xhr.send(null);
  }

  function getPrevious() {
    console.log('prev')
  }

  function getNext() {
    console.log('next')
  }

  return (
    <>
      <div>
        {
          list.map((item) => {
            return (
              <Item key={item.name} name={item.name} ></Item>
            );
          })
        }

      </div>

      <div className="flex justify-center mt-10">
        <Button func={() => getPrevious()} content="Previous" />
        <Button func={() => getNext()} content="Next" />
      </div>  
    </>
  );
}

export default Table;
