    }
  }

  function getPrevious() {
    console.log('Previous')
  }

  function getNext() {
    console.log('next')
  }

  return (
    <>
      <div>
        {
          list.map((item) => {
            return (
              <Item key={item.name} name={item.name} ></Item>
            );
          })
        }

      </div>

      <div className="flex justify-center mt-10">
        {!!buttons[0] && <Button func={() => getPrevious()} content="Previous" />}
        {!!buttons[1] && <Button func={() => getNext()} content="Next" /> }
      </div>  
    </>
  );
}

export default Table;
